<!DOCTYPE html><html xmlns="http://www.w3.org/1999/html" ng-app="orderApp"><head>    <meta http-equiv="content-type" content="text/html; charset=UTF-8">    <base href="/">    <title>Pizza Store</title>    <link href="assets/css/app.css" rel="stylesheet" type="text/css"/>    <link href="assets/css/bootstrap.min.css" rel="stylesheet" type="text/css"/>    <link href='http://fonts.googleapis.com/css?family=Open+Sans:400,700' rel='stylesheet' type='text/css'/>    <script src="assets/js/jquery-1.11.3.min.js" type="application/javascript"></script>    <script src="assets/js/bootstrap.min.js" type="application/javascript"></script>    <script src="assets/js/angular/angular.min.js" type="application/javascript"></script>    <script src="assets/js/angular/angular-resource.min.js" type="application/javascript"></script>    <script src="assets/app/app.js"></script>    <script src="assets/app/order/orderSvc.js"></script>    <script src="assets/app/order/orderCtrl.js"></script>    <script src="assets/js/moment.min.js"></script></head><body ng-controller="orderCtrl"><main role="main">    <div class="text-center"><img src="assets/images/AjaxLoader.gif" ng-hide="isLoaded"></div>    <div class="container-fluid" ng-hide="!isLoaded">        <div class="row-fluid">            <div class="col-xs-6">                <div class="row" ng-repeat="itemGroup in items">                    <div class="col-xs-4" ng-repeat="item in itemGroup">                        <button class="btn-danger itemButton"                                ng-click="addItem(item)">                            {{item.name}}                        </button>                    </div>                </div>            </div>            <div class="col-xs-6">                <div class="row">                    <div class="col-xs-12" style="margin-left:300px">                        <button class="btn btn-default" data-toggle="modal" data-target="#ordersModal">ORDERS</button>                    </div>                </div>                <div class="row" style="font-weight: 700">                    <div class="col-xs-12"><span ng-show="order.orderNumber == null">New Order</span>                        <span ng-hide="order.orderNumber == null">Order: #{{order.orderNumber}}</span>                    </div>                </div>                <div class="row" style="border: 1px solid black; min-height: 400px; width: 400px">                    <!--                    <div id="border" style="border: 1px solid black; min-height: 400px; width: 400px">-->                    <div class="col-xs-12 text-center" ng-show="isEmpty(orderLineItems)"                         style="font-style: italic">No items have been added                    </div>                    <div class="row orderLineItem" ng-repeat="orderLineItem in orderLineItems track by $index"                         ng-click="setSelectedOrderItem(orderLineItem)"                         ng-class="{selected : selectedOrderItem.itemId == orderLineItem.itemId}">                        <div class="col-xs-1">{{orderLineItem.qty}}</div>                        <div class="col-xs-5">{{orderLineItem.name}}</div>                        <div class="col-xs-6">{{orderLineItem.price}}</div>                    </div>                    <div id="relative">                        <div class="row" style="margin-top: 15px">                            <div class="col-xs-4 orderInfo">Sub Total:</div>                            <div class="col-xs-8">{{order.subTotal}}</div>                        </div>                        <div class="row">                            <div class="col-xs-4 orderInfo">Sales Tax:</div>                            <div class="col-xs-8">{{order.taxTotal}}</div>                        </div>                        <div class="row">                            <div class="col-xs-4 orderInfo">Grand Total:</div>                            <div class="col-xs-8">{{order.grandTotal}}</div>                        </div>                        <div id="tenderRecord" ng-hide="order.orderNumber == null">                            <div class="row">                                <div class="col-xs-4 orderInfo">Amount                                    Tendered:                                </div>                                <div class="col-xs-8">{{order.amountTendered}}</div>                            </div>                            <div class="row">                                <div class="col-xs-4 orderInfo">Change Due:                                </div>                                <div class="col-xs-8">{{order.changeGiven}}</div>                            </div>                        </div>                    </div>                </div>                <div class="row" ng-hide="isEmpty(orderLineItems)" style="margin-top: 15px">                    <div class="col-xs-2">                        <button class="btn btn-default" ng-click="voidItem()"                                ng-disabled="order.orderNumber != null || isEmpty(selectedOrderItem)">VOID                        </button>                    </div>                    <div class="col-xs-10">                        <button class="btn btn-default" data-toggle="modal" data-target="#payModal"                                ng-disabled="order.orderNumber !== null">PAY NOW                        </button>                    </div>                </div>            </div>        </div></main><!-- Pay Modal --><div id="payModal" class="modal fade" role="dialog">    <div class="modal-dialog">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 class="modal-title">Tender Payment</h4>            </div>            <div class="modal-body">                <div>                    <div class="container">                        <div class="row">                            <div class="col-xs-2">Amount Due:</div>                            <div class="col-xs-10">{{order.grandTotal}}</div>                        </div>                        <div class="row">                            <div class="col-xs-2">Amount Tendered:</div>                            <div class="col-xs-10"><input type="number" ng-model="order.amountTendered"                                                          ng-change="setChangeGiven()"></div>                        </div>                        <div class="row">                            <div class="col-xs-2">Change Due</div>                            <div class="col-xs-10">{{order.changeGiven}}</div>                        </div>                    </div>                </div>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-default" data-dismiss="modal" ng-click="clearTenderRecord()">                    CANCEL                </button>                <button type="button" class="btn btn-default" ng-disabled="order.amountTendered <= order.grandTotal"                        ng-click="createTenderRecord(order)" data-dismiss="modal">                    TENDER                </button>            </div>        </div>    </div></div><!-- Orders Modal --><div id="ordersModal" class="modal fade" role="dialog">    <div class="modal-dialog modal-lg">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 class="modal-title">Orders</h4>            </div>            <div class="modal-body">                <button ng-repeat="(key, filterButton) in filterButtons" ng-model="filterButtons" ng-click="updateFilter(filterButton.name)"                        ng-class="{'active':filterButton.selected }" class="btn btn-default">{{filterButton.displayName}}                </button>                <button class="btn btn-default pull-right" style="margin-left:15px" data-toggle="modal"                        data-target="#importItemsModal">                    IMPORT ITEMS                </button>                <button type="button" class="btn btn-default pull-right" data-dismiss="modal"                        ng-click="startNewOrder()">                    NEW ORDER                </button>                    <div class="text-center" style="margin-top: 15px; margin-bottom: 15px; font-style: italic" ng-show="filteredOrders.length == 0">                        No orders found                    </div>                    <table class="table table-bordered table-hover" style="margin-top: 15px;" ng-hide="filteredOrders.length == 0">                        <thead>                        <tr>                            <th>Status</th>                            <th>Timestamp</th>                            <th>Order Number</th>                            <th>Total</th>                        </tr>                        </thead>                        <tbody>                        <tr ng-repeat="order in filteredOrders track by $index" ng-click="getOrder(order.orderId)"                            data-dismiss="modal">                            <td>{{order.status}}</td>                            <td>{{order.timestamp}}</td>                            <td>{{order.orderNumber}}</td>                            <td>{{order.grandTotal}}</td>                        </tr>                        </tbody>                    </table>                <div class="modal-footer">                    <button type="button" class="btn btn-default" data-dismiss="modal">CLOSE</button>                </div>            </div>        </div>    </div></div><!-- Import Items Modal --><div id="importItemsModal" class="modal fade" role="dialog">    <div class="modal-dialog">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal" onclick="cancelImport()">&times;</button>                <h4 class="modal-title">Import Items</h4>            </div>            <div class="modal-body">                <span class="btn btn-default btn-file">                    BROWSE... <input type="file" name="importFile" accept="text/csv">                </span>                <span id="selectedFileName"></span>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-default" data-dismiss="modal" onclick="cancelImport()">                    CANCEL                </button>                <button type="button" class="btn btn-default" data-dismiss="modal" ng-click="importItems()">RUN</button>            </div>        </div>    </div></div></body></html>