<!DOCTYPE html><html xmlns="http://www.w3.org/1999/html" ng-app="orderApp"><head>    <meta http-equiv="content-type" content="text/html; charset=UTF-8">    <base href="/">    <title>Pizza Store</title>    <link href="assets/css/app.css" rel="stylesheet" type="text/css"/>    <link href="assets/css/bootstrap.min.css" rel="stylesheet" type="text/css"/>    <link href='http://fonts.googleapis.com/css?family=Open+Sans:400,700' rel='stylesheet' type='text/css'/>    <script src="assets/js/jquery-1.11.3.min.js" type="application/javascript"></script>    <script src="assets/js/bootstrap.min.js" type="application/javascript"></script>    <script src="assets/js/angular/angular.min.js" type="application/javascript"></script>    <script src="assets/js/angular/angular-resource.min.js" type="application/javascript"></script>    <script src="assets/js/ui-bootstrap-0.13.1.min.js"></script>    <script src="assets/js/moment.min.js" type="application/javascript"></script>    <script src="assets/app/app.js" type="application/javascript"></script>    <script src="assets/app/order/orderFilter.js" type="application/javascript"></script>    <script src="assets/app/order/orderSvc.js" type="application/javascript"></script>    <script src="assets/app/order/orderCtrl.js" type="application/javascript"></script>    <script src="assets/app/order/ordersModalCtrl.js"></script>    <script src="assets/app/order/directives/ordersGrid.js"></script></head><body ng-controller="orderCtrl"><main role="main">    <div class="text-center"><img src="assets/images/AjaxLoader.gif" ng-hide="isLoaded"></div>    <div class="container-fluid" ng-hide="!isLoaded">        <div class="row-fluid">            <div class="col-xs-6">                <div class="row" ng-repeat="itemGroup in items" ng-model="items">                    <div class="col-xs-4" ng-repeat="item in itemGroup">                        <button class="btn-danger itemButton"                                ng-click="addItem(item)" ng-disabled="order.orderNumber != null">                            {{item.id}}: {{item.name}}                        </button>                    </div>                </div>            </div>            <div class="col-xs-6">                <div class="row">                    <div class="col-xs-12" style="margin-left:300px">                        <button class="btn btn-default" ng-click="viewOrders()">ORDERS</button>                    </div>                </div>                <div class="row" style="font-weight: 700">                    <div class="col-xs-12"><span ng-show="order.orderNumber == null">New Order</span>                        <span ng-hide="order.orderNumber == null">Order: #{{order.orderNumber}}</span>                    </div>                </div>                <div class="row" style="border: 1px solid black; min-height: 400px; width: 400px">                    <!--                    <div id="border" style="border: 1px solid black; min-height: 400px; width: 400px">-->                    <div class="col-xs-12 text-center" ng-show="isEmpty(orderLineItems)"                         style="font-style: italic">No items have been added                    </div>                    <div class="row orderLineItem" ng-repeat="orderLineItem in orderLineItems track by $index"                         ng-click="setSelectedOrderItem(orderLineItem)"                         ng-class="{selected : selectedOrderItem.itemId == orderLineItem.itemId}">                        <div class="col-xs-1">{{orderLineItem.qty}}</div>                        <div class="col-xs-5">{{orderLineItem.name}}</div>                        <div class="col-xs-6">{{orderLineItem.price}}</div>                    </div>                    <div id="relative">                        <div class="row" style="margin-top: 15px">                            <div class="col-xs-4 orderInfo">Sub Total:</div>                            <div class="col-xs-8">{{order.subTotal}}</div>                        </div>                        <div class="row">                            <div class="col-xs-4 orderInfo">Sales Tax:</div>                            <div class="col-xs-8">{{order.taxTotal}}</div>                        </div>                        <div class="row">                            <div class="col-xs-4 orderInfo">Grand Total:</div>                            <div class="col-xs-8">{{order.grandTotal}}</div>                        </div>                        <div id="tenderRecord" ng-hide="order.orderNumber == null">                            <div class="row">                                <div class="col-xs-4 orderInfo">Amount                                    Tendered:                                </div>                                <div class="col-xs-8">{{order.amountTendered}}</div>                            </div>                            <div class="row">                                <div class="col-xs-4 orderInfo">Change Due:                                </div>                                <div class="col-xs-8">{{order.changeGiven}}</div>                            </div>                        </div>                    </div>                </div>                <div class="row" ng-hide="isEmpty(orderLineItems)" style="margin-top: 15px">                    <div class="col-xs-2">                        <button class="btn btn-default" ng-click="voidItem()"                                ng-disabled="order.orderNumber != null || isEmpty(selectedOrderItem)">VOID                        </button>                    </div>                    <div class="col-xs-10">                        <button class="btn btn-default" data-toggle="modal" data-target="#payModal"                                ng-disabled="order.orderNumber != null">PAY NOW                        </button>                    </div>                </div>            </div>        </div></main><!-- Pay Modal --><div id="payModal" class="modal fade" role="dialog">    <div class="modal-dialog">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 class="modal-title">Tender Payment</h4>            </div>            <div class="modal-body">                <div>                    <div class="container">                        <div class="row">                            <div class="col-xs-2">Amount Due:</div>                            <div class="col-xs-10">{{order.grandTotal}}</div>                        </div>                        <div class="row">                            <div class="col-xs-2">Amount Tendered:</div>                            <div class="col-xs-10"><input type="number" ng-model="order.amountTendered"                                                          ng-change="setChangeGiven()"></div>                        </div>                        <div class="row">                            <div class="col-xs-2">Change Due</div>                            <div class="col-xs-10">{{order.changeGiven}}</div>                        </div>                    </div>                </div>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-default" data-dismiss="modal" ng-click="clearTenderRecord()">                    CANCEL                </button>                <button type="button" class="btn btn-default" ng-disabled="order.amountTendered <= order.grandTotal"                        ng-click="createTenderRecord(order)" data-dismiss="modal">                    TENDER                </button>            </div>        </div>    </div></div><!-- Orders Modal --></body></html>